import '../sass/App.scss'
import React, { useState} from 'react'
function App() {
  const [toDo, setToDo] = useState('')
  let  [toDos, setToDos] = useState([])

  function adding(){
    setToDos((prevState) => {
    return [...prevState, toDo]
    })
  }
  return (
    <div className="App">
      {/* Header */}
     <div className="header">
       <h1 className="mainHeader">To Do List </h1>
       <h2 className="subHeader">A React App</h2>
     </div>

    {/* Adding */}
     <div className="adding">
         <div className="title">Add a todo list :</div>
         <input type="text" className="toDo" onChange = {(e) => setToDo(e.target.value)}/>
         <div className="button" onClick={adding}>+</div>
       </div>

       {/* Display */}

       <div className="display">
         {toDos.map((todo) => {
           return(
             <div className='todo'>{todo}</div>
           )
         })}
       </div>
    </div>
  );
}

export default App;
