import '../sass/App.scss'
import React, { useState} from 'react'
function App() {

  // useStates
  const [toDo, setToDo] = useState('')
  let  [toDos, setToDos] = useState([])
  let [done, setDone] = useState(false)

  // functions
    function adding(){
    setToDos((prevState) => {
    return [...prevState, toDo]
    }) 
  }
  function deleteFunction(todo){
   let newToDo = toDos.filter((item) => item !== todo )
   setToDos(newToDo)
  }
  function doneFunction(todo){
     let newToDo = toDos.filter((item) => {
       if(item == todo){
         setDone(!done)
       }
     } )
     setToDos(newToDo)
  }
  return (
    <div className="App">
      {/* Header */}
     <div className="header">
       <h1 className="mainHeader">To Do List </h1>
       <h2 className="subHeader">A React App</h2>
     </div>

    {/* Adding */}
     <div className="adding">
         <div className="title">Add a todo list :</div>
         <input type="text" className="toDo" onChange = {(e) => setToDo(e.target.value)}/>
         <div className="button" onClick={adding}>+</div>
       </div>

       {/* Display */}

       <div className="display">
         {toDos.map((todo) => {
           return(
             <div className='todo'>
               <div className='text' id={done ? "yes" : "no"}>{todo}</div>
               <div className="buttons">
                 <div className="delete" onClick = {() => deleteFunction(todo)}>delete</div>
                 <div className="toggle" onClick ={() =>doneFunction(todo)}>Cross out</div>
               </div>
             </div>
           )
         })}
       </div>
    </div>
  );
}

export default App;



